import React, { useState } from 'react';
import './App.css'
import Header from './Header'
import ListOfContacts from './ListOfContacts'
import PortalChatBox from './PortalChatBox';
import ChatBox from './ChatBox';

const App = () => {
  const [headerDetails, setHeaderDetails] = useState({})
  const [showModal, setShowModal] = useState({showChatBox: false, to: {}, from: {}});
  const handleCloseModal = () => setShowModal({showChatBox: false, to: {}, from: {}});

  return (

    // <ChatBox />
    <div className="App">
      <Header headerDetails={headerDetails}/>
      <ListOfContacts setShowModal={setShowModal} headerDetails={headerDetails} setHeaderDetails={setHeaderDetails}/>
      {
        showModal.showChatBox ?
        (<PortalChatBox onClose={handleCloseModal} chatFrom={showModal.from} chatTo={showModal.to}/>) : null
      }
    </div>
  );
}

export default App;
