import React, { useState } from 'react';
import './App.css'
import Header from './Header'
import ListOfContacts from './ListOfContacts'
import PortalChatBox from './PortalChatBox';
// import ChatBox from './ChatBox';

const App = () => {
  const [headerDetails, setHeaderDetails] = useState({})
  const [showModal, setShowModal] = useState({showChatBox: false, to: {}});
  const handleCloseModal = () => setShowModal({showChatBox: false, to: {}});
  console.log(showModal);
  return (
    <div className="App">
      <Header headerDetails={headerDetails}/>
      <ListOfContacts setShowModal={setShowModal} headerDetails={headerDetails} setHeaderDetails={setHeaderDetails}/>
      {
        showModal.showChatBox ?
        (<PortalChatBox onClose={handleCloseModal} chatFrom={headerDetails} chatTo={showModal.to}/>) : null
      }
    </div>
  );
}

export default App;
